import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Producto } from 'src/app/interfaces/producto';
import { ListProductosComponent } from '../list-productos/list-productos.component';
import { ProductosService } from 'src/app/services/productos.service';
import { ActivatedRoute, Router } from '@angular/router';
import { ToastrService } from 'ngx-toastr';

@Component({
  selector: 'app-add-edit-producto',
  templateUrl: './add-edit-producto.component.html',
  styleUrls: ['./add-edit-producto.component.css']
})
export class AddEditProductoComponent {
  formProducto:FormGroup;
  loading:boolean=false;

  constructor(private fb:FormBuilder, 
    private _productService:ProductosService,
    private router:Router,
    private toastr:ToastrService,
    private aRouter:ActivatedRoute){
    this.formProducto=this.fb.group({
      //Para poner mas de una validacion hay que ponerlas entre claudators
      name:['',Validators.required],
      quantity:[null,Validators.required],
      price:[null,Validators.required],
      totalPrice:[null,Validators.required],
    })
    aRouter.snapshot.paramMap.get('id');
    console.log(aRouter.snapshot.paramMap.get('id'));
  }

  addProducto(){
    const product:Producto={
      name:this.formProducto.value.name,
      quantity:this.formProducto.value.quantity,
      price:this.formProducto.value.price,
      totalprice:this.formProducto.value.totalPrice,

    }

    this.loading=true;
    this._productService.crateProduco(product).subscribe(()=>{
      this.loading=false;
      this.toastr.success(`El producto ${product.name} fue registrado con exito`,'Producto agregado')
      this.router.navigate(['/']);
    })

  }

}
<app-progress-bar *ngIf="loading"></app-progress-bar>
<div *ngIf="!loading" class="row mt-1">
    <div class="col-lg-4 offset-lg-4">
        <div class="card bg-secondary mb-3" style="max-width: 25rem;">
            <div class="card-header text-center">Agregar producto</div>
            <div class="card-body">
                <form [formGroup]="formProducto" (ngSubmit)="addProducto()">
                    <input formControlName="name" type="text" class="form-control" placeholder="Ingrese nombre">
                    <span *ngIf="formProducto.get('name')?.hasError('required') && formProducto.get('name')?.touched">
                        El campo nombre es <strong>requerido</strong>
                    </span>
                    <input formControlName="quantity" type="number" class="form-control mt-2" placeholder="Ingrese cantidad comprado">
                    <span *ngIf="formProducto.get('quantity')?.hasError('required') && formProducto.get('quantity')?.touched">
                        El campo cantidad es <strong>requerido</strong>
                    </span>
                    <input formControlName="price" type="number" class="form-control mt-2" placeholder="Ingrese precio individual">
                    <span *ngIf="formProducto.get('price')?.hasError('required') && formProducto.get('price')?.touched">
                        El campo precio es <strong>requerido</strong>
                    </span>
                    <input formControlName="totalPrice" type="numer" class="form-control mt-2" placeholder="Ingrese precio total">
                    <div class="row mt-2 text-center">
                        <div class="col-lg-6">
                            <button routerLink="/" type="button" class="btn btn-secondary">Volver</button>
                        </div>
                        <div class="col-lg-6">
                            <button [disabled]="formProducto.invalid" type="submit" class="btn btn-primary">Agregar</button>
                        </div>
                    </div>
                </form>
            </div>

        </div>
    </div>
</div>
